import Module from'./module';
import ModuleLoader from './moduleloader';
import path from 'path';
// NOTE this uses the package.json in the process root
const modules = require(path.join(process.cwd(), 'package.json')).dependencies;

/**
 * The main application class
 */
export default class App extends Module {
  /**
  * Initialises the application
  * @return {Promise}
  */
  initialise() {
    /**
    * Reference to the ModuleLoader instance.
    * @type {ModuleLoader}
    */
    this.moduleloader = new ModuleLoader(modules, this);
    return new Promise((resolve, reject) => {
      this.moduleloader.initialiseModules().then(resolve).catch(reject);
    });
  }

  preload(app, resolve, reject) {
    this.moduleloader.preloadModules().then(resolve).catch(reject);
  }

  boot(app, resolve, reject) {
    this.moduleloader.bootModules().then(resolve).catch(reject);
  }
}

import EventEmitter from 'events';

/**
 * Abstract class for authoring tool modules
 */
export default class Module extends EventEmitter {
  /**
  * @constructor
  * @param {Object} app Reference to the main application
  * @param {Object} config Config object from package.json for this module
  */
  constructor(app, config) {
    super();
    /**
    * Whether the Module has been preloaded
    * @type {Boolean}
    */
    this.hasPreloaded = false;
    /**
    * Whether the Module has been booted
    * @type {Boolean}
    */
    this.hasBooted = false;

    Object.defineProperty(this, 'pkgConfig', {
      get: () => { return config; }
    });
  }

  /**
  * Getter function to return module name (inferred from Class name)
  * @return {String} Name of module
  */
  get name() {
    return this.constructor.name;
  }

  /**
  * Function to call any actions required to preload the module. Will be passed as a promise executor to preloadDelegate
  * @param {Object} app reference to the main app
  * @param {Function} resolve resolves the promise
  * @param {Function} reject rejects the promise
  */
  preload(app, resolve, reject) {
    resolve();
  }
  /**
  * Preloads the module. Acts as a wrapper function to preload.
  * @param {Object} app reference to the main app
  * @return {Promise}
  */
  preloadDelegate(app) {
    return new Promise((resolve, reject) => {
      this.preload.call(this, app, resolve, reject);
    }).then(() => {
      this.hasPreloaded = true;
      this.emit('preloaded');
    });
  }

  /**
  * Function to call any actions required to start the module. Will be passed as a promise executor to preloadDelegate
  * @param {Object} app reference to the main app
  * @param {Function} resolve resolves the promise
  * @param {Function} reject rejects the promise
  */
  boot(app, resolve, reject) {
    resolve();
  }
  /**
  * Boots the module. Acts as a wrapper function to boot.
  * @param {Object} app reference to the main app
  * @return {Promise}
  */
  bootDelegate(app) {
    return new Promise((resolve, reject) => {
      this.boot.call(this, app, resolve, reject);
    }).then(() => {
      this.hasBooted = true;
      this.emit('booted');
    });
  }
}

import path from 'path';
/**
* Handles the loading of Adapt authoring tool module dependencies.
*/
export default class ModuleLoader {
  /**
  * @constructor
  * @param {Object} dependencies List of module dependencies (in package.json format)
  * @param {Object} app Used to store module references
  */
  constructor(dependencies, app) {
    /**
    * List of dependent modules in NPM package.json dependencies format
    * @type {object}
    */
    this.dependencies = dependencies;
    /**
    * Reference to the main app
    * @type {App}
    */
    this.app = app;
    /**
    * List of module instances
    * @type {Module[]}
    */
    this.modules = [];
  }
  /**
  * Initialises all defined dependent modules
  * @return {Promise}
  */
  initialiseModules() {
    return new Promise((resolve, reject) => {
      Promise.all(Object.keys(this.dependencies).map(this.initialiseModule.bind(this)))
        .then(resolve).catch(reject);
    });
  }
  /**
  * Initialises a single module
  * @param {Object} moduleName Name of the module to initialise
  * @return {Promise}
  */
  initialiseModule(moduleName) {
    return new Promise((resolve, reject) => {
      const pkgDir = path.join(process.cwd(), 'node_modules', moduleName, 'package.json');
      // First check it's a valid authoring tool module using package.json
      try {
        var moduleConfig = require(pkgDir).adapt_authoring;
        if(!moduleConfig.module) return resolve();
      } catch(e) {
        return resolve();
      }
      // now try and import the module, and make sure it provides a function export
      try {
        var ModClass = require(moduleName);

        if(typeof ModClass !== 'function') {
          console.warn(`'${moduleName}' is not a valid module`);
          return resolve();
        }
        var instance = new ModClass(this.app, moduleConfig);

      } catch(e) {
        console.log(`Failed to load '${moduleName}', ${e}`);
        resolve();
      }
      // store any modules that get this far and include in the preload/boot process
      this.modules.push(instance);
      resolve();
    });
  }
  /**
  * Preloads all defined dependent modules
  * @return {Promise}
  */
  preloadModules() {
    return this.callFunctionOnModules('preloadDelegate');
  }
  /**
  * Boots all defined dependent modules
  * @return {Promise}
  */
  bootModules() {
    return this.callFunctionOnModules('bootDelegate');
  }

  /**
  * @ignore
  * Asynchronously calls a function on all this.modules
  */
  callFunctionOnModules(funcName) {
    return new Promise((resolve, reject) => {
      Promise.all(this.modules.map(instance => {
        return new Promise((nestResolve, nestReject) => {
          if(!instance[funcName]) return nestResolve();
          instance[funcName](this.app).then(nestResolve).catch(nestReject);
        });
      })).then(resolve).catch(reject);
    });
  }
}

