import DataTypes from 'adapt-authoring-core';
const {Module} = DataTypes;
/**
* An example authoring tool plugin. Exposes a 'helloworld' API route.
*/
export class HelloWorld extends Module {
  /**
  * Creates the custom router
  */
  preload(app, resolve, reject) {
    if(!app.server) {
      console.warn(`${this.name}: Cannot initialise, server isn't running`);
      resolve();
    }
    /**
    * Instance of the Express server
    * @type {Server}
    */
    this.server = app.server;

    this.server.createRouter('/helloworld')
      .use((req, res, next) => {
        console.log('Hello world middleware');
        next();
      })
      .get('/', (req, res) => res.send('Hello World!'));

    resolve();
  }
}

import {DataTypes} from 'adapt-authoring-core';
import chalk from 'chalk';
const {Module} = Datatypes;

/**
* Simple logging library
*/
export class Logger extends Module {
  /**
  * Logs a message to the console
  * @param {string} type The type of log. Accepts: warn, error, log.
  * @param {*} msg The message to log
  * @param {*} args Any other arguments
  */
  log(type, msg, ...args) {
    var type = colourise(type.toUpperCase(), type);
    var timestamp = (new Date()).toISOString();
    console.log(`${type} [${timestamp}]: ${msg}`);
  }
}
const COLOUR_MAP = {
  warn: chalk.yellow,
  error: chalk.red
};

function colourise(str, type) {
  const func = COLOUR_MAP[type];
  if(!func) return str;
  return func(str);
}

